mapper.select:
select
id, code, name, abbreviation, description, college_type, college_name, type, type_name, study_cycle, status,
create_time, update_time, created_by, modified_by, istest
from iquanwai.lesson_package


        and id = #{id}


        and code = #{code}


        and name = #{name}


        and abbreviation = #{abbreviation}


        and description = #{description}


        and college_type = #{collegeType}


        and college_name = #{collegeName}


        and type = #{type}


        and type_name = #{typeName}


        and study_cycle = #{studyCycle}


        and status = #{status}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and created_by = #{createdBy}


        and modified_by = #{modifiedBy}


        and istest = #{istest}

select a.acquire_id , a.delay_time , case when a.status = 0 then '生效中' when a.status = 1 then '已软删' else '未知状态' end as status , c.name as lessonPackageName , case when b.type = 0 then '黑名单' when b.type = 1 then '白名单' else '未知状态' end as type , b.lesson_package_id , b.lesson_package_code , case when b.registration_status = 0 then '有效学籍（全局互斥）' when b.registration_status = 1 then '代开营' when b.registration_status = 2 then '已开营' when b.registration_status = 3 then '已结营' else '未知状态' end as registration_status , b.status , date_format(b.create_time,'%Y-%m-%d %T')as create_time from iquanwai.lesson_package_acquire_rule_relation a left join iquanwai.lesson_package_acquire_rule b on a.acquire_id = b.id join iquanwai.lesson_package c on b.lesson_package_id = c.id
where a.status = 0 and a.lesson_package_code = #{lessonPackageCode}
and
#{acquireLessonPackageCode}
